mapper.select:
SELECT m.* FROM menu m
WHERE 1=1

    AND m.id=#{id}


    AND m.title=#{title}


    AND m.create_time=#{createTime}


    AND m.creater_id=#{createrId}

SELECT m.* FROM menu m
WHERE 1=1

    AND m.id=#{id}


    AND m.title=#{title}


    AND m.create_time=#{createTime}


    AND m.creater_id=#{createrId}


    limit #{startNum},#{pageSize}
