mapper.select:
select longid AS longId, username AS username , password AS
password ,real_name AS realName FROM user limit
#{startNum},#{pageSize}

SELECT longid AS longId, username AS username , password AS
password ,real_name AS realName FROM user WHERE username=#{username}
AND
password=#{password}

mapper.update:
UPDATE user SET
longid=#{longId}, username =#{username}, password=#{password},
real_name=#{realName}
WHERE
username=#{username}
